# Simple TODO List Manager
class TodoManager:
    def __init__(self):
        self.tasks = []

    def add_task(self, description, priority="medium"):
        """Add a new task to the list"""
        task = {
            "id": len(self.tasks) + 1,
            "description": description,
            "priority": priority,
            "completed": False,
            "created_at": datetime.now().isoformat()
        }
        self.tasks.append(task)
        return task

    def complete_task(self, task_id):
        """Mark a task as completed"""
        for task in self.tasks:
            if task["id"] == task_id:
                task["completed"] = True
                return True
        return False

    def delete_task(self, task_id):
        """Delete a task from the list"""
        self.tasks = [t for t in self.tasks if t["id"] != task_id]

    def get_tasks(self, completed=None):
        """Get all tasks or filter by completion status"""
        if completed is None:
            return self.tasks
        return [t for t in self.tasks if t["completed"] == completed]

# Example usage:
todo = TodoManager()
todo.add_task("Learn Python programming", "high")
todo.add_task("Build a web application", "medium")
print("Current tasks:", todo.get_tasks(completed=False))

